// ==================================================================
// LOGIQUE DE G√âN√âRATION DE DONN√âES AVANC√âE
// Simule une arythmie ventriculaire critique 30% du temps.
// La SPECIALTY est incluse pour le routage par le Load Balancer.
// ==================================================================

// G√©n√®re un signal ECG de 187 points (similaire √† numpy)
function generate_ecg_signal(condition) {
    const signal_length = 187;
    const signal = [];

    for (let i = 0; i < signal_length; i++) {
        let t = i / signal_length;
        let value = 0;

        if (condition === 'normal') {
            // Forme d'onde normale simplifi√©e
            value = (
                0.5 * Math.sin(2 * Math.PI * 5 * t) + 
                1.0 * Math.exp(-Math.pow((t - 0.2) / 0.05, 2)) + 
                2.0 * Math.exp(-Math.pow((t - 0.4) / 0.03, 2)) - 
                0.8 * Math.exp(-Math.pow((t - 0.6) / 0.07, 2)) 
            );
        } else { // 'ventricular' ou critique
            // Forme d'onde arythmie (QRS large)
            value = (
                0.3 * Math.sin(2 * Math.PI * 3 * t) + 
                2.5 * Math.exp(-Math.pow((t - 0.3) / 0.08, 2)) - 
                0.4 * Math.exp(-Math.pow((t - 0.7) / 0.1, 2)) + 
                1.2 * Math.exp(-Math.pow((t - 0.5) / 0.05, 2))
            );
        }

        // Ajout d'un petit bruit al√©atoire
        value += (Math.random() - 0.5) * 0.1; 
        signal.push(parseFloat(value.toFixed(4))); 
    }
    return signal;
}

// G√©n√®re des signes vitaux coh√©rents
function get_vital_signs(condition) {
    if (condition === 'normal') {
        return {
            'heart_rate': Math.floor(Math.random() * (100 - 60) + 60),
            'temperature': (Math.random() * (37.2 - 36.0) + 36.0).toFixed(1),
            'spo2': Math.floor(Math.random() * (100 - 95) + 95)
        };
    } else { // Condition critique (ventricular)
        return {
            'heart_rate': Math.floor(Math.random() * (180 - 140) + 140), 
            'temperature': (Math.random() * (38.5 - 37.0) + 37.0).toFixed(1), 
            'spo2': Math.floor(Math.random() * (95 - 85) + 85) 
        };
    }
}

// --- LOGIQUE GLOBALE ---
const patient_data = [
    {id: "P-001", name: "JEAN DUPONT", specialty: "general"},
    {id: "P-002", name: "MARIE MARTIN", specialty: "critical_care"},
    {id: "P-003", name: "PIERRE LEROY", specialty: "pediatric"}
];

const patient = patient_data[Math.floor(Math.random() * patient_data.length)];

// 30% de chance d'une arythmie critique
const condition = Math.random() < 0.7 ? 'normal' : 'ventricular';

const signal = generate_ecg_signal(condition);
const vital_signs = get_vital_signs(condition);

const alert_status = (condition === 'ventricular') ? "CRITIQUE" : "Normal";
const alert_color = (condition === 'ventricular') ? "#FF0000" : "#008000"; // Rouge vif ou Vert fonc√©

// --- CONSTRUCTION DU PAYLOAD FINAL (pour l'API) ---
msg.payload = {
    "device_id": "NODE-RED-DEV-001",
    "patient_id": patient.id,
    "patient_name": patient.name,
    "signal": signal, 
    "timestamp": new Date().toISOString(),
    "condition": condition, 
    "specialty": patient.specialty, 
    "heart_rate": vital_signs.heart_rate, 
    "temperature": vital_signs.temperature, 
    "spo2": vital_signs.spo2
};

// --- SORTIES POUR LA VISUALISATION (Dashboard) ---
const msg_temp = { topic: "temp_final", payload: vital_signs.temperature, unit: "¬∞C" };
const msg_spo2 = { topic: "spo2_final", payload: vital_signs.spo2, unit: "%" };
const msg_hr = { topic: "hr_final", payload: vital_signs.heart_rate, unit: "BPM" };

const msg_alert = { 
    topic: "alert_final", 
    payload: alert_status,
    color: alert_color
};

const msg_ecg_viz = { topic: "ecg_viz_final", payload: signal };

// Sortie 1: API | Sortie 2: Temp | Sortie 3: SpO2 | Sortie 4: HR | Sortie 5: Alerte (Texte) | Sortie 6: ECG (Graphe)
return [msg, msg_temp, msg_spo2, msg_hr, msg_alert, msg_ecg_viz];
